#!/usr/bin/env node
const inquirer = require("inquirer");
const command = require("./templates/command");
const newProject = require("./templates/project");
const event = require("./templates/event");
const chalk = require('chalk')
console.log(chalk.yellow('Welcome To DJSCLI, Coded By Mech'))
inquirer
  .prompt([
    {
      type: "list",
      message: "Please choose an action",
      name: "options",
      choices: ["Create A New Discord.js Bot From Scratch", "Create A New Discord Bot Command", "Create A New Discord.js Event"],
    },
  ])
  .then(({ options }) => {
    if (options === "Create A New Discord Bot Command") {
        inquirer.prompt([
            {
                type: 'input',
                name: 'name',
                message: 'Please Enter Your Command Name:'
            },
            {
                type: 'input',
                name: 'category',
                message: 'Please Enter Your Command Category:'
            },
            {
                type: 'input',
                name: 'description',
                message: 'Please Enter Your Command Description:'
            },
            {
                type: 'input',
                name: 'usage',
                message: 'Please Enter Your Command Name (Including ${prefix}):'
            },
        ]).then(answers2 => {
            command(answers2.name, answers2.category, answers2.description, answers2.usage)
        })
    } else if (options === "Create A New Discord.js Bot From Scratch") {
      inquirer
        .prompt([
          {
            type: "input",
            name: "botToken",
            message: "What is the bot's token?",
          },
          {
            type: "input",
            name: "botPrefix",
            message: "What is the bot's prefix?",
          },
        ])
        .then(({ botToken, botPrefix }) => {
          newProject(botToken, botPrefix);
        });
    } else {
      inquirer
        .prompt([
          {
            type: "list",
            message: "Please choose a type of event",
            name: "init",
            choices: ["Client", "Guild"],
          },
        ])
        .then(({ init }) => {
          event(init)
        });
    }
  });
